    public void metamorphose(int transformation_percentage){
        int x,y,rand;

        for(y=0;y<HEIGHT;y++){
            for(x=0;x<WIDTH;x++){
                rand = (int)(Math.random() * 101);
                System.out.println(rand);
                if(rand < transformation_percentage){
                    cells[y][x].setState(pick_state(cells[y][x].getState()));
                }
            }
        }
    }

